<script>
export default {
    name: 'PreLoader',
    mounted() {
        var that = this;
        document.addEventListener("DOMContentLoaded", function(){
            var tl = new TimelineMax({repeat: 0, repeatDelay: 5})
            tl
            .to('#preload_dot', 0.8, { morphSVG: '#preload_line' }, '+=0.1')
            .to('#preload_dot', 1.2, { morphSVG: '#preload_hermitage' }, '+=0')
            .add('#preload_home_interiors', 0)
            .to('#preload_home_interiors', 0, { y: '+=10', transformOrigin:"center center" })
            .to('#preload_home_interiors', 1.5, { autoAlpha: 1, y: '-=10', transformOrigin:"center center" })
            .to('#preload_logo', 6, { scale: 1.1, transformOrigin: 'center center' }, '-=3')
            .to('#preload_logo', 1, { opacity: 0 }, '-=2')
            
            setTimeout(function(){
                var hideLogo = new TimelineMax();
                hideLogo
                .to('#preload_logo', 0.5, { autoAlpha: 0, onComplete: loadPage })
                .to('.preload_bg', 0.2, { scaleY: 0.001, transformOrigin: 'center center' })
                .to('.preload_bg', 0.3, { scaleX: 0, transformOrigin: 'center center' })
                .to('.preload_bg', 0, { zIndex: 0 })
                .to('#preload_logo', 0, { zIndex: 0 })
            }, 5000)
        });
        function loadPage() {
            that.$emit('preloder-complete');
        }
    }
}
</script>